do
for sess in sess1 sess2
do
for sub in R02 R03 R05 R06 R07 R08 R09 R10
do
  regdir=${MAINDIR}/${sub}/${sess}/nii/r${run}_e2_cropped_nan_reg.feat/reg
  
  featdir_e2=`ls -d ${MAINDIR}/${sub}/${sess}/nii/r${run}*e2*.feat_maxextent`
